import {useEffect} from 'react';

import styles from '../styles/Home.module.css';

import AppLayout from '../components/app-layout';
import Link from 'next/link';
import Router from 'next/router';

import axios from 'axios';

const Home = function() {
  const recommentItemClick = (item) => {
    Router.push({
      pathname: "/recommend",
      query: {
        id: item
      }
    })
  }

  return (
    <div>
      {/* <a href="/about">关于</a>
      <Link href="/about">
        <a>关于</a>
      </Link> */}

      <h1 className={styles.title}>Home页面</h1>
      <h2>Banner</h2>
      <h2>推荐数据</h2>
      <ul>
        {
          [1, 2, 3].map((item, index) => {
            return (
              // <Link key={item} href={`/recommend?id=${item}`}>
              //   <li>推荐数据id:{item}</li>
              // </Link>
              <li key={item} onClick={e => recommentItemClick(item)}>推荐数据id:{item}</li>
            )
          })
        }
      </ul>
    </div>
  )
}

Home.getInitialProps = async (props) => {
  const res = await axios({url: "http://123.207.32.32:8000/home/multidata"});

  return {
    name: "why"
  }
}

export default Home;
